import React, { useState } from "react";
import { createClient } from "@supabase/supabase-js";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";
import { useEffect } from "react";


const supabaseUrl = "https://pxclcppdiirguhcjbmqq.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InB4Y2xjcHBkaWlyZ3VoY2pibXFxIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTE0MTY5MDYsImV4cCI6MjA2Njk5MjkwNn0.WQWpSH1cL_LAL3NBK-ZCYCzOL_V7oDHo99jnyilBqeM";
const supabase = createClient(supabaseUrl, supabaseKey);

export default function App() {
    const controls = useAnimation();
    const [ref, inView] = useInView({
      triggerOnce: true,
      threshold: 0.2, // reveals when 20% of element visible
    });

    useEffect(() => {
      if (inView) {
        controls.start({ opacity: 1, y: 0 });
        }
    }, [controls, inView]);

  const [formData, setFormData] = useState({
    aadhar_number: "",
    full_name: "",
    dob: "",
    iti_name: "",
    trade_name: "",
    training_period: "1 Year",
    passing_year: "",
    phone_number: "",
    current_address: "",
    naps_registered: "",
  });

  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "phone_number") {
      const sanitized = value.replace(/\D/g, "").slice(0, 10);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else if (name === "aadhar_number") {
      const sanitized = value.replace(/\D/g, "").slice(0, 12);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else if (name === "passing_year") {
      const sanitized = value.replace(/\D/g, "").slice(0, 4);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.from("personal_details").insert([formData]);
    setLoading(false);
    if (!error) {
      setShowModal(true);
      setFormData({
        aadhar_number: "",
        full_name: "",
        dob: "",
        iti_name: "",
        trade_name: "",
        training_period: "1 Year",
        passing_year: "",
        phone_number: "",
        current_address: "",
        naps_registered: "",
      });
      setTimeout(() => setShowModal(false), 2500);
    } else {
      console.error("Supabase Insert Error:", error);
      alert("Submission failed: " + error.message);
    }
  };

  return (
    <div
      className="flex items-center justify-center min-h-screen p-4 bg-gradient-to-br from-purple-100 to-white"
    >
      <motion.div
        className="bg-white p-6 sm:p-8 rounded-2xl shadow-xl max-w-lg w-full"
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-2xl sm:text-3xl font-bold text-center text-purple-700 mb-4">
          Registration for ITI Apprenticeship in Nagpur
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          {[
            { label: "Aadhar Card Number", name: "aadhar_number", type: "text", placeholder: "Aadhar Card Number" },
            { label: "Full Name", name: "full_name", type: "text", placeholder: "Full Name" },
            { label: "Date Of Birth", name: "dob", type: "date", placeholder: "" },
            { label: "Name of ITI with Taluka and District", name: "iti_name", type: "text", placeholder: "ITI Name with Taluka & District" },
            { label: "Name of Trade", name: "trade_name", type: "text", placeholder: "Name of Trade" },
            { label: "ITI Passing Year / Exam Appeared Year", name: "passing_year", type: "text", placeholder: "Passing/Appeared Year" },
            { label: "Phone Number", name: "phone_number", type: "text", placeholder: "Phone Number" },
          ].map((field) => (
            <div key={field.name}>
              <label className="block text-gray-700 font-medium mb-1">{field.label}</label>
              <input
                type={field.type}
                name={field.name}
                placeholder={field.placeholder}
                required
                value={formData[field.name]}
                onChange={handleChange}
                className="border p-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
              />
            </div>
          ))}

          <div>
            <label className="block text-gray-700 font-medium mb-1">Training Period</label>
            <select
              name="training_period"
              required
              value={formData.training_period}
              onChange={handleChange}
              className="border p-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            >
              <option value="1 Year">1 Year</option>
              <option value="2 Year">2 Year</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-1">Current Address</label>
            <textarea
              name="current_address"
              placeholder="Current Address"
              required
              value={formData.current_address}
              onChange={handleChange}
              className="border p-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            ></textarea>
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-1">NAPS Portal Registered</label>
            <div className="flex space-x-4 mt-1">
              {["Yes", "No"].map((option) => (
                <label key={option} className="flex items-center space-x-2">
                  <input
                    type="radio"
                    name="naps_registered"
                    value={option}
                    checked={formData.naps_registered === option}
                    onChange={handleChange}
                    required
                  />
                  <span>{option}</span>
                </label>
              ))}
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 bg-gradient-to-r from-purple-500 to-indigo-500 text-white rounded-full font-semibold transition transform hover:scale-105 active:scale-95"
          >
            {loading ? "Submitting..." : "Submit Details"}
          </button>
        </form>

        {showModal && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50"
          >
            <div className="bg-white p-6 rounded-xl shadow-lg text-center">
              <h2 className="text-lg font-semibold text-green-600 mb-2">âœ… Submitted!</h2>
              <p className="text-gray-600">Form submitted successfully.</p>
            </div>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
}
