import React, { useState, useEffect } from "react";
import { createClient } from "@supabase/supabase-js";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

// Supabase setup
const supabaseUrl = "https://pxclcppdiirguhcjbmqq.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InB4Y2xjcHBkaWlyZ3VoY2pibXFxIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTE0MTY5MDYsImV4cCI6MjA2Njk5MjkwNn0.WQWpSH1cL_LAL3NBK-ZCYCzOL_V7oDHo99jnyilBqeM";
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [formData, setFormData] = useState({
    aadhar_number: "",
    full_name: "",
    dob: "",
    iti_name: "",
    trade_name: "",
    training_period: "1 Year",
    passing_year: "",
    phone_number: "",
    current_address: "",
    naps_registered: "",
  });
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "phone_number") {
      const sanitized = value.replace(/\D/g, "").slice(0, 10);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else if (name === "aadhar_number") {
      const sanitized = value.replace(/\D/g, "").slice(0, 12);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else if (name === "passing_year") {
      const sanitized = value.replace(/\D/g, "").slice(0, 4);
      setFormData((prev) => ({ ...prev, [name]: sanitized }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.from("personal_details").insert([formData]);
    setLoading(false);
    if (!error) {
      setShowModal(true);
      setFormData({
        aadhar_number: "",
        full_name: "",
        dob: "",
        iti_name: "",
        trade_name: "",
        training_period: "1 Year",
        passing_year: "",
        phone_number: "",
        current_address: "",
        naps_registered: "",
      });
      setTimeout(() => setShowModal(false), 2500);
    } else {
      alert("Submission failed: " + error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-lightbg p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl max-w-md w-full border border-gray-200">
        <h1 className="text-2xl font-bold text-center text-navy mb-4">
          YES Apprenticeship Registration
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          {[
            { label: "Aadhar Card Number", name: "aadhar_number", type: "text", placeholder: "Aadhar Card Number" },
            { label: "Full Name", name: "full_name", type: "text", placeholder: "Full Name" },
            { label: "Date of Birth", name: "dob", type: "date", placeholder: "" },
            { label: "Name of ITI with Taluka and District", name: "iti_name", type: "text", placeholder: "ITI Name with Taluka and District" },
            { label: "Name of Trade", name: "trade_name", type: "text", placeholder: "Trade Name" },
            { label: "ITI Passing Year / Exam Appeared Year", name: "passing_year", type: "text", placeholder: "Passing Year" },
            { label: "Phone Number", name: "phone_number", type: "text", placeholder: "Phone Number" },
          ].map((field, idx) => (
            <div key={idx}>
              <label className="block text-darknavy font-medium mb-1">{field.label}</label>
              <input
                type={field.type}
                name={field.name}
                placeholder={field.placeholder}
                required
                value={formData[field.name]}
                onChange={handleChange}
                className="border p-3 rounded-lg w-full transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-mustard hover:border-mustard hover:bg-yellow-50"
              />
            </div>
          ))}

          <div>
            <label className="block text-darknavy font-medium mb-1">Training Period</label>
            <select
              name="training_period"
              required
              value={formData.training_period}
              onChange={handleChange}
              className="border p-3 rounded-lg w-full transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-mustard hover:border-mustard hover:bg-yellow-50"
            >
              <option value="1 Year">1 Year</option>
              <option value="2 Year">2 Year</option>
            </select>
          </div>

          <div>
            <label className="block text-darknavy font-medium mb-1">Residing Address</label>
            <textarea
              name="current_address"
              placeholder="Current Address"
              required
              value={formData.current_address}
              onChange={handleChange}
              className="border p-3 rounded-lg w-full transition duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-mustard hover:border-mustard hover:bg-yellow-50"
            ></textarea>
          </div>

          <div>
            <label className="block text-darknavy font-medium mb-2">NAPS Portal Registered</label>
            <div className="flex space-x-6">
              {["Yes", "No"].map((option) => (
                <label key={option} className="flex items-center space-x-2">
                  <input
                    type="radio"
                    name="naps_registered"
                    value={option}
                    checked={formData.naps_registered === option}
                    onChange={handleChange}
                    className="form-radio accent-mustard"
                  />
                  <span>{option}</span>
                </label>
              ))}
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 bg-mustard text-darknavy rounded-full font-semibold transition duration-300 ease-in-out transform hover:scale-105 active:scale-95 hover:bg-yellow-400"
          >
            {loading ? "Submitting..." : "Submit Details"}
          </button>
        </form>

        {showModal && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          >
            <div className="bg-white p-6 rounded-xl shadow-lg text-center">
              <h2 className="text-xl font-semibold text-green-600 mb-2">✅ Submitted!</h2>
              <p className="text-gray-600">Form submitted successfully.</p>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
}

export default App;
