import React, { useState } from "react";
import { createClient } from "@supabase/supabase-js";

// Replace these with your Supabase project details
const supabaseUrl = "YOUR_SUPABASE_URL";
const supabaseKey = "YOUR_SUPABASE_ANON_KEY";
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [formData, setFormData] = useState({
    full_name: "",
    phone_number: "",
    dob: "",
    qualification: "",
    permanent_address: "",
    current_address: "",
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setFormData((prev) => ({
        ...prev,
        current_address: checked ? prev.permanent_address : "",
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const { error } = await supabase.from("personal_details").insert([formData]);
    setLoading(false);
    if (!error) {
      setSuccess(true);
      setFormData({
        full_name: "",
        phone_number: "",
        dob: "",
        qualification: "",
        permanent_address: "",
        current_address: "",
      });
    } else {
      alert("Submission failed. Please try again.");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen p-4">
      <div className="bg-white p-8 rounded-2xl shadow-md max-w-md w-full">
        <h1 className="text-2xl font-bold text-center text-purple-600 mb-2">Nandini Manpower Services</h1>
        <p className="text-center text-gray-500 mb-6">Please fill out the form below.</p>
        {success && <div className="text-green-600 mb-4 text-center">Form submitted successfully!</div>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="text" name="full_name" placeholder="Full Name" required value={formData.full_name} onChange={handleChange} className="border p-2 rounded w-full"/>
          <input type="text" name="phone_number" placeholder="Phone Number" required value={formData.phone_number} onChange={handleChange} className="border p-2 rounded w-full"/>
          <input type="date" name="dob" required value={formData.dob} onChange={handleChange} className="border p-2 rounded w-full"/>
          <input type="text" name="qualification" placeholder="Qualification" required value={formData.qualification} onChange={handleChange} className="border p-2 rounded w-full"/>
          <textarea name="permanent_address" placeholder="Permanent Address" required value={formData.permanent_address} onChange={handleChange} className="border p-2 rounded w-full"></textarea>
          <label className="flex items-center space-x-2">
            <input type="checkbox" onChange={handleChange} />
            <span>Current address is same as permanent address</span>
          </label>
          <textarea name="current_address" placeholder="Current Address" required value={formData.current_address} onChange={handleChange} className="border p-2 rounded w-full"></textarea>
          <button type="submit" disabled={loading} className="bg-gradient-to-r from-purple-500 to-blue-500 text-white py-2 rounded w-full">
            {loading ? "Submitting..." : "Submit Details"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
